storeMapping = {
    'msg': store[0],
    'cmd': store[1],
    'rebt': store[2],
    'stdn': store[3],
}

# 格式化要发送的消息
def formatB4Send(content):
    arr = []
    for ch in content:
        tmp = ''.join(list(map(lambda x: hex(ord(x)), ch)))
        if int(tmp, 16) > 0xff:
            tmp = tmp[2:]
            high = int((tmp[0] + tmp[1]), 16)
            low = int((tmp[2] + tmp[3]), 16)
            arr.append(low)
            arr.append(high)
        else:
            high = 0
            low = int((tmp[2] + tmp[3]), 16)
            arr.append(low)
            arr.append(high)
    return arr

def pkgSendList(cmdtype, content):
    arrs = formatB4Send(content)
    if cmdtype == 'msg':
        index = 56
        result = storeMapping['msg']
        for elem in arrs:
            result[index] = elem
            index += 1
    elif cmdtype == 'cmd':
        index = 578
        result = storeMapping['cmd']
        for elem in arrs:
            result[index] = elem
            index += 1
    return result

# 发送
def send(ip,port,send_list, client):
    for abc in send_list:
        payload = pack("%dB" % (len(abc)), *abc)
        client.sendto(payload, (ip,int(port)))


def createSendList(msg:str=None,cmd:str=None,rebt=None,stdn=None):
    send_list = []
    if msg:
        send_list.append(pkgSendList('msg', msg))
    if cmd:
        send_list.append(pkgSendList('cmd', cmd))
    if rebt:
        send_list.append(storeMapping['rebt'])
    if stdn:
        send_list.append(storeMapping['stdn'])
    return send_list

class sendObject():
    def __init__(self,address,cmd=[], msg=[], rebt=False, stdn=False, ipPrefix=None, excludeIP=None):
        self.sendList = []
        self.ipPrefix = ipPrefix
        self.excludeIP = excludeIP
        self.setSend(cmd, msg, rebt, stdn, address)

    def initUDP(self,udpclient): # 对 deepcopy 的支持
        self.client = udpclient

    def setSend(self, cmd=[], msg=[], rebt=False, stdn=False, address=None):
        self.cmd = cmd
        self.msg = msg
        self.rebt = rebt
        self.stdn = stdn
        if cmd:
            for item in self.cmd:
                self.sendList.append(pkgSendList("cmd", item))
        if msg:
            for item in self.msg:
                self.sendList.append(pkgSendList("msg", item))
        if stdn:
            self.sendList.append(storeMapping['stdn'])  # 关机优先级高于重启
        elif rebt:
            self.sendList.append(storeMapping['rebt'])
        
        if address:
            self.ip,self.port = address

    def clearSend(self):
        self.sendList = []

    def getSend(self):
        return self.sendList

    def send(self):
        if not self.ip:
            return
        if self.ip == "broadcast":
            send("<broadcast>", self.port, self.sendList, self.client)
        if self.ip == "random":
            randomIP = f"{self.ipPrefix}{random.randint(0,255)}"
            if randomIP not in self.excludeIP:
                send(randomIP, self.port, self.sendList, self.client)
        elif type(self.ip) == str:
            ips = self.ip.split(";")
            for item in ips:
                if item:
                    send(item, self.port, self.sendList, self.client)
